<? include("header.php");     $tekst = $HTTP_POST_VARS["tekst"];    if ($tekst) {        $fp = fopen("notes", "a");        fputs($fp, date("d/m/Y")."\n");        fputs($fp, date("H.i")."\n");        fputs($fp, stripslashes(str_replace("\r", "", str_replace("\n", "", nl2br($tekst))))."\n");        fclose($fp);    }    $n = 0;    $guestbook = array();    $fp = fopen("notes", "r");    while (!feof($fp)) {        $datum = fgets($fp, 1024);        $tijd = fgets($fp, 1024);        $tekst = fgets($fp, 1024);        if ($datum && $tijd && $tekst) {            $guestbook[$n][0] = $datum;			$guestbook[$n][1] = $tijd;            $guestbook[$n][2] = $tekst;            $n++;        }    }    fclose($fp);	    if ($kill) {      $fp = fopen("notes", "w");      for($i = 0; $i < sizeof($guestbook); $i++) {	  if ( $i != ($kill - 1)) {        fputs($fp, $guestbook[$i][0]);                   fputs($fp, $guestbook[$i][1]);                  fputs($fp, $guestbook[$i][2]);		}      }      fclose($fp);	  	$n = 0;    $guestbook = array();    $fp = fopen("notes", "r");    while (!feof($fp)) {        $datum = fgets($fp, 1024);        $tijd = fgets($fp, 1024);        $tekst = fgets($fp, 1024);        if ($datum && $tijd && $tekst) {            $guestbook[$n][0] = $datum;			$guestbook[$n][1] = $tijd;            $guestbook[$n][2] = $tekst;            $n++;        }    }    fclose($fp);	}	?><br><table border="0" cellpadding="2" cellspacing="2" width="500"><? 	for ($i = 0; $i < sizeof($guestbook) ; $i++) {   $j = $n - $i - 1;  echo "<tr><td bgcolor=eeeeee>".$guestbook[$j][0]." ".$guestbook[$j][1]." ( <a href=index.php?kill=".($j + 1).">delete</a> )</td></tr>";  echo "<tr><td>".$guestbook[$j][2]."</td></tr>";}?><tr><td align="center"><br><br><form name="frmentry" action="index.php"  method="post"><textarea name="tekst" class="formAreaBig" rows="3"></textarea><br><br><a href="javascript:document.frmentry.submit()">add note</a></f