<? include("header.php") ?><script language="javascript">    function toevoegen()    {        document.frmadres.actie.value = "toevoegen";        document.frmadres.submit();    }    function verwijderen()    {        document.frmadres.actie.value = "verwijderen";        document.frmadres.submit();    }    function adres(s)    {        document.frmadres.emailadres.value = s;    }	</script><?    function schrijfadressen($adressen)     {        $fp = fopen("links", "w");        for($i = 0; $i < sizeof($adressen); $i++)            fputs($fp, $adressen[$i]);        fclose($fp);    }        function adreslink($adres)    {        echo "<a href=\"javascript:adres('$adres')\">$adres</a>";    }    /* Initialisatie. */    $day = date('j');    $month = $maandnamen[date('n')];    $year = date('y');    $actie = $HTTP_POST_VARS["actie"];    $onderwerp = stripcslashes($HTTP_POST_VARS["onderwerp"]);    $tekst = stripcslashes($HTTP_POST_VARS["tekst"]);    $emailadres = stripcslashes($HTTP_POST_VARS["emailadres"]);    /* Lees de adressen. */    $n = 0;    $adressen = array();        $fp = fopen("links", "r");    while (!feof($fp)) {        $adres = fgets($fp, 1024);        if ($adres != "") {            $adressen[$n] = $adres;            $n++;        }    }    fclose($fp);        /* Het toevoegen en verwijderen van adressen. */    if ($actie == "toevoegen" || $actie == "verwijderen") {        if ($emailadres == "")            $actie = "";        else {            if ($actie == "toevoegen") {                $adressen[sizeof($adressen)] = $emailadres."\n";                schrijfadressen($adressen);            }            elseif ($actie == "verwijderen") {                $key = array_search($emailadres."\n", $adressen);                if (!($key === false)) {                    $adressen[$key] = $adressen[sizeof($adressen) - 1];                    $adressen = array_slice($adressen, 0, sizeof($adressen) - 1);                    schrijfadressen($adressen);                }            }        }    }      sort($adressen);?><br><br><table width="500" cellspacing="2" cellpadding="2" border="0"><?	$i = 0;     while ($i < sizeof($adressen)) { ?>         <tr>		 <td><a href="http://<? echo $adressen[$i] ?>" target="_blank"><? if ( $adressen[$i] != "" ) echo "link" ?></a>          <? echo adreslink($adressen[$i++]) ?></td>		 <td><a href="http://<? echo $adressen[$i] ?>" target="_blank"><? if ( $adressen[$i] != "" ) echo "link" ?></a>          <? echo adreslink($adressen[$i++]) ?></td>		 <td><a href="http://<? echo $adressen[$i] ?>" target="_blank"><? if ( $adressen[$i] != "" ) echo "link" ?></a>          <? echo adreslink($adressen[$i++]) ?></td><?  } ?><tr><td colspan="3" align="center"><br><br>  <form name="frmadres" action="links.php" method="post">   <input type="text" name="emailadres" maxlength="70" class="formText">   <input type="hidden" name="actie" value="toevoegen">  </form> <a href="javascript:toevoegen()">add</a> | <a href="javascript:verwijderen()">delete</a> </td></tr></table></td></tr></table></body></html>